net.rptools.maptool.model.MacroButtonPro.command:
[h: Success=input("curSkill|"+SkillList+"|Skill:|LIST","Mods | 0 | Modifiers to skill","Cap | 0 | Skill cap (0 for none)")]
[h: abort(Success)]
[h: curLevel=listGet(SkillLevels,curSkill)]
[r: listGet(SkillList,curSkill)]
[r,IF(Cap==0),CODE:{
[r:skill(curLevel,Mods)]
};{
[r:skill(curLevel,Mods,Cap)]
}]

[h:statList="ST,Dexterity,IQ,Health,Will,Fear,Perception,Vision,Hearing"]
[h:statListNiceNames="ST,DX,IQ,HT,Will,Fear,Perception,Vision,Hearing"]
[h: Success=input("curStat|"+statListNiceNames+"|Stat:|LIST","Mods | 0 | Modifiers to stat roll","Cap| 0 | Roll cap (0 for none)")]
[h: abort(Success)]
[r: listGet(statList,curStat)] check 
[h: curLevel = getProperty(listget(statList,curStat))]
[R,IF(Cap==0),CODE:{
[R:skill(curLevel,Mods)]
};{
[R:skill(curLevel,Mods,Cap)]
}]

[h:HitLocation = getStrProp(macro.args, "HitLocation")]
[r,switch(HitLocation), code:
case "random":	{[r:macro.return = 0]};
case "chest": 	{[r:macro.return = 0]};
case "abdomen": 	{[r:macro.return = -1]};
case "torso": 	{[r:macro.return = 0]};
case "vitals": 	{[r:macro.return = -3]};
case "arm":		{[r:macro.return = -2]};
case "leg":		{[r:macro.return = -2]};
case "foot":   	{[r:macro.return = -4]};
case "hand":  	{[r:macro.return = -4]};
case "face":  	{[r:macro.return = -5]};
case "neck":  	{[r:macro.return = -5]};
case "brain":  	{[r:macro.return = -7]};
case "skull":  	{[r:macro.return = -7]};
case "eye":		{[r:macro.return = -9]};
case "ear":		{[r:macro.return = -7]};
case "nose":		{[r:macro.return = -7]};
case "jaw":		{[r:macro.return = -6]};
case "spine":		{[r:macro.return = -8]};
case "neck vein":		{[r:macro.return = -8]};
case "limb vein":		{[r:macro.return = -5]};
case "heart":		{[r:macro.return = -5]};
case "digestive tract":		{[r:macro.return = -3]};
case "pelvis":		{[r:macro.return = -2]};
case "groin":		{[r:macro.return = -3]};
default:      		{[r:macro.return = "ERROR "+HitLocation+" NOT FOUND"]}
]
